/**
 * Replaces unprintable characters by their escaped (or unicode escaped)
 * equivalents in the given string
 */
protected static final String addEscapes(String str) {
    StringBuffer retval = new StringBuffer();
    char ch;
    for (int i = 0; i < str.length(); i++) {
        switch(str.charAt(i)) {
            case 0:
                continue;
            case '\b':
                retval.append("\\b");
                continue;
            case '\t':
                retval.append("\\t");
                continue;
            case '\n':
                retval.append("\\n");
                continue;
            case '\f':
                retval.append("\\f");
                continue;
            case '\r':
                retval.append("\\r");
                continue;
            case '\"':
                retval.append("\\\"");
                continue;
            case '\'':
                retval.append("\\\'");
                continue;
            case '\\':
                retval.append("\\\\");
                continue;
            default:
                if ((ch = str.charAt(i)) < 0x20 || ch > 0x7e) {
                    String s = "0000" + Integer.toString(ch, 16);
                    retval.append("\\u" + s.substring(s.length() - 4, s.length()));
                } else {
                    retval.append(ch);
                }
                continue;
        }
    }
    return retval.toString();
}

/**
 * Returns a detailed message for the Error when it is thrown by the token
 * manager to indicate a lexical error. Parameters : EOFSeen : indicates if
 * EOF caused the lexical error curLexState : lexical state in which this
 * error occurred errorLine : line number when the error occurred
 * errorColumn : column number when the error occurred errorAfter : prefix
 * that was seen before this error occurred curchar : the offending
 * character Note: You can customize the lexical error message by modifying
 * this method.
 */
protected static String LexicalError(boolean EOFSeen, int lexState, int errorLine, int errorColumn, String errorAfter, char curChar) {
    return ("Lexical error at line " + errorLine + ", column " + errorColumn + ".  Encountered: " + (EOFSeen ? "<EOF> " : ("\"" + addEscapes(String.valueOf(curChar)) + "\"") + " (" + (int) curChar + "), ") + "after : \"" + addEscapes(errorAfter) + "\"");
}

/**
 * Used to convert raw characters to their escaped version when these raw
 * version cannot be used as part of an ASCII string literal.
 */
static String add_escapes(String str) {
    StringBuffer retval = new StringBuffer();
    char ch;
    for (int i = 0; i < str.length(); i++) {
        switch(str.charAt(i)) {
            case 0:
                continue;
            case '\b':
                retval.append("\\b");
                continue;
            case '\t':
                retval.append("\\t");
                continue;
            case '\n':
                retval.append("\\n");
                continue;
            case '\f':
                retval.append("\\f");
                continue;
            case '\r':
                retval.append("\\r");
                continue;
            case '\"':
                retval.append("\\\"");
                continue;
            case '\'':
                retval.append("\\\'");
                continue;
            case '\\':
                retval.append("\\\\");
                continue;
            default:
                if ((ch = str.charAt(i)) < 0x20 || ch > 0x7e) {
                    String s = "0000" + Integer.toString(ch, 16);
                    retval.append("\\u" + s.substring(s.length() - 4, s.length()));
                } else {
                    retval.append(ch);
                }
                continue;
        }
    }
    return retval.toString();
}

/**
 * Returns a new Token object, by default. However, if you want, you can
 * create and return subclass objects based on the value of ofKind. Simply
 * add the cases to the switch for all those special cases. For example, if
 * you have a subclass of Token called IDToken that you want to create if
 * ofKind is ID, simply add something like :
 *
 * case MyParserConstants.ID : return new IDToken(ofKind, image);
 *
 * to the following switch statement. Then you can cast matchedToken
 * variable to the appropriate type and use sit in your lexical actions.
 */
public static Token newToken(int ofKind, String image) {
    switch(ofKind) {
        default:
            return new Token(ofKind, image);
    }
}

public static Token newToken(int ofKind) {
    return newToken(ofKind, null);
}

/**
 * Count step contains in a file.
 *
 * @param path
 *            path to the file
 * @return count of step event in the file
 * @throws IOException
 * @see org.graphstream.stream.file.FileSourceFactory
 */
public static int countStepInFile(String path) throws IOException {
    StepCounter counter = new StepCounter();
    FileSource source = FileSourceFactory.sourceFor(path);
    source.addElementSink(counter);
    source.readAll(path);
    return counter.getStepCount();
}

@SuppressWarnings("unchecked")
protected static HashMap<String, Object> GET(HttpExchange ex) {
    HashMap<String, Object> get = new HashMap<String, Object>();
    String[] args = ex.getRequestURI().getRawQuery().split("[&]");
    for (String arg : args) {
        String[] kv = arg.split("[=]");
        String k, v;
        k = null;
        v = null;
        try {
            if (kv.length > 0)
                k = URLDecoder.decode(kv[0], System.getProperty("file.encoding"));
            if (kv.length > 1)
                v = URLDecoder.decode(kv[1], System.getProperty("file.encoding"));
            if (get.containsKey(k)) {
                Object o = get.get(k);
                if (o instanceof LinkedList<?>)
                    ((LinkedList<Object>) o).add(v);
                else {
                    LinkedList<Object> l = new LinkedList<Object>();
                    l.add(o);
                    l.add(v);
                    get.put(k, l);
                }
            } else {
                get.put(k, v);
            }
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
        }
    }
    return get;
}

/**
 * Looks at the file name given and its extension and propose a file output
 * for the format that match this extension.
 *
 * @param filename
 *            The file name where the graph will be written.
 * @return A file sink or null.
 */
public static FileSink sinkFor(String filename) {
    if (filename.indexOf('.') > 0) {
        String ext = filename.substring(filename.lastIndexOf('.') + 1);
        ext = ext.toLowerCase();
        if (ext2sink.containsKey(ext)) {
            Class<? extends FileSink> fsink = ext2sink.get(ext);
            try {
                return fsink.newInstance();
            } catch (InstantiationException e) {
                e.printStackTrace();
            } catch (IllegalAccessException e) {
                e.printStackTrace();
            }
        }
    }
    return null;
}

