/**
 * Decodes data from Base64 notation, automatically
 * detecting gzip-compressed data and decompressing it.
 *
 * @param s the string to decode
 * @return the decoded data
 * @throws java.io.IOException If there is a problem
 * @since 1.4
 */
public static byte[] decode(String s) throws java.io.IOException {
    return decode(s, NO_OPTIONS);
}

/**
 * Decodes data from Base64 notation, automatically
 * detecting gzip-compressed data and decompressing it.
 *
 * @param s the string to decode
 * @param options encode options such as URL_SAFE
 * @return the decoded data
 * @throws java.io.IOException if there is an error
 * @throws NullPointerException if <tt>s</tt> is null
 * @since 1.4
 */
public static byte[] decode(String s, int options) throws java.io.IOException {
    if (s == null) {
        throw new NullPointerException("Input string was null.");
    }
    // end if
    byte[] bytes;
    try {
        bytes = s.getBytes(PREFERRED_ENCODING);
    }// end try
     catch (java.io.UnsupportedEncodingException uee) {
        bytes = s.getBytes();
    }
    // end catch
    //</change>
    // Decode
    bytes = decode(bytes, 0, bytes.length, options);
    // Check to see if it's gzip-compressed
    // GZIP Magic Two-Byte Number: 0x8b1f (35615)
    boolean dontGunzip = (options & DONT_GUNZIP) != 0;
    if ((bytes != null) && (bytes.length >= 4) && (!dontGunzip)) {
        int head = ((int) bytes[0] & 0xff) | ((bytes[1] << 8) & 0xff00);
        if (java.util.zip.GZIPInputStream.GZIP_MAGIC == head) {
            java.io.ByteArrayInputStream bais = null;
            java.util.zip.GZIPInputStream gzis = null;
            java.io.ByteArrayOutputStream baos = null;
            byte[] buffer = new byte[2048];
            int length = 0;
            try {
                baos = new java.io.ByteArrayOutputStream();
                bais = new java.io.ByteArrayInputStream(bytes);
                gzis = new java.util.zip.GZIPInputStream(bais);
                while ((length = gzis.read(buffer)) >= 0) {
                    baos.write(buffer, 0, length);
                }
                // end while: reading input
                // No error? Get new bytes.
                bytes = baos.toByteArray();
            }// end try
             catch (java.io.IOException e) {
                e.printStackTrace();
                // Just return originally-decoded bytes
            } finally // end catch
            {
                try {
                    baos.close();
                } catch (Exception e) {
                }
                try {
                    gzis.close();
                } catch (Exception e) {
                }
                try {
                    bais.close();
                } catch (Exception e) {
                }
            }
            // end finally
        }
        // end if: gzipped
    }
    // end if: bytes.length >= 2
    return bytes;
}

// end decode
/**
 * Attempts to decode Base64 data and deserialize a Java
 * Object within. Returns <tt>null</tt> if there was an error.
 *
 * @param encodedObject The Base64 data to decode
 * @return The decoded and deserialized object
 * @throws NullPointerException if encodedObject is null
 * @throws java.io.IOException if there is a general error
 * @throws ClassNotFoundException if the decoded object is of a
 *         class that cannot be found by the JVM
 * @since 1.5
 */
public static Object decodeToObject(String encodedObject) throws java.io.IOException, java.lang.ClassNotFoundException {
    return decodeToObject(encodedObject, NO_OPTIONS, null);
}

/**
 * Attempts to decode Base64 data and deserialize a Java
 * Object within. Returns <tt>null</tt> if there was an error.
 * If <tt>loader</tt> is not null, it will be the class loader
 * used when deserializing.
 *
 * @param encodedObject The Base64 data to decode
 * @param options Various parameters related to decoding
 * @param loader Optional class loader to use in deserializing classes.
 * @return The decoded and deserialized object
 * @throws NullPointerException if encodedObject is null
 * @throws java.io.IOException if there is a general error
 * @throws ClassNotFoundException if the decoded object is of a
 *         class that cannot be found by the JVM
 * @since 2.3.4
 */
public static Object decodeToObject(String encodedObject, int options, final ClassLoader loader) throws java.io.IOException, java.lang.ClassNotFoundException {
    // Decode and gunzip if necessary
    byte[] objBytes = decode(encodedObject, options);
    java.io.ByteArrayInputStream bais = null;
    java.io.ObjectInputStream ois = null;
    Object obj = null;
    try {
        bais = new java.io.ByteArrayInputStream(objBytes);
        // If no custom class loader is provided, use Java's builtin OIS.
        if (loader == null) {
            ois = new java.io.ObjectInputStream(bais);
        } else // end if: no loader provided
        // Else make a customized object input stream that uses
        // the provided class loader.
        {
            ois = new java.io.ObjectInputStream(bais) {

                @Override
                public Class<?> resolveClass(java.io.ObjectStreamClass streamClass) throws java.io.IOException, ClassNotFoundException {
                    Class<?> c = Class.forName(streamClass.getName(), false, loader);
                    if (c == null) {
                        return super.resolveClass(streamClass);
                    } else {
                        // Class loader knows of this class.
                        return c;
                    }
                    // end else: not null
                }
            };
            // end ois
        }
        // end else: no custom class loader
        obj = ois.readObject();
    }// end try
     catch (java.io.IOException e) {
        // Catch and throw in order to execute finally{}
        throw e;
    }// end catch
     catch (java.lang.ClassNotFoundException e) {
        // Catch and throw in order to execute finally{}
        throw e;
    } finally // end catch
    {
        try {
            bais.close();
        } catch (Exception e) {
        }
        try {
            ois.close();
        } catch (Exception e) {
        }
    }
    // end finally
    return obj;
}

