public static <T extends Element> Filter<T> falseFilter() {
    return new Filter<T>() {

        public boolean isAvailable(T e) {
            return false;
        }
    };
}

public static <T extends Element> Filter<T> trueFilter() {
    return new Filter<T>() {

        public boolean isAvailable(T e) {
            return true;
        }
    };
