/**
 * Create a file input for the given file name.
 *
 * <p>
 * This method first tests if the file is a regular file and is readable. If
 * so, it opens it and reads the magic cookie to test the known file formats
 * that can be inferred from their header. If it works, it returns a file
 * input for the format. Else it looks at the file name extension, and
 * returns a file input for the extension. Finally if all fail, it throws a
 * NotFoundException.
 * </p>
 *
 * <p>
 * Notice that this method only creates the file input and does not connect
 * it to a graph.
 * </p>
 *
 * @param fileName
 *            Name of the graph file.
 * @return A graph reader suitable for the fileName graph format.
 * @throws IOException
 *             If the file is not readable or accessible.
 */
public static FileSource sourceFor(String fileName) throws IOException {
    File file = new File(fileName);
    if (!file.isFile())
        throw new IOException("not a regular file '" + fileName + "'");
    if (!file.canRead())
        throw new IOException("not a readable file '" + fileName + "'");
    // Try to read the beginning of the file.
    RandomAccessFile in = new RandomAccessFile(fileName, "r");
    byte[] b = new byte[10];
    int n = in.read(b, 0, 10);
    // System.err.printf( "[" );
    // for( int i=0; i<n; ++i )
    // {
    // System.err.printf( "%c", (char)b[i] );
    // }
    // System.err.printf( "]%n" );
    in.close();
    // Surely match a DGS file, as DGS files are well done and have a
    // signature.
    if (n >= 3 && b[0] == 'D' && b[1] == 'G' && b[2] == 'S') {
        if (n >= 6 && b[3] == '0' && b[4] == '0') {
            if (b[5] == '1' || b[5] == '2') {
                return new FileSourceDGS1And2();
            } else if (b[5] == '3' || b[5] == '4') {
                return new FileSourceDGS();
            }
        }
    }
    // Maybe match a GML file as most GML files begin by the line "graph [",
    // but not sure, you may create a GML file that starts by a comment, an
    // empty line, with any kind of spaces, etc.
    if (n >= 7 && b[0] == 'g' && b[1] == 'r' && b[2] == 'a' && b[3] == 'p' && b[4] == 'h' && b[5] == ' ' && b[6] == '[') {
        return new org.graphstream.stream.file.FileSourceGML();
    }
    if (n >= 4 && b[0] == '(' && b[1] == 't' && b[2] == 'l' && b[3] == 'p')
        return new FileSourceTLP();
    // The web reader.
    String flc = fileName.toLowerCase();
    // If we did not found anything, we try with the filename extension ...
    if (flc.endsWith(".dgs")) {
        return new FileSourceDGS();
    }
    if (flc.endsWith(".gml") || flc.endsWith(".dgml")) {
        return new org.graphstream.stream.file.FileSourceGML();
    }
    if (flc.endsWith(".net")) {
        return new FileSourcePajek();
    }
    if (flc.endsWith(".chaco") || flc.endsWith(".graph")) {
        // return new GraphReaderChaco();
    }
    if (flc.endsWith(".dot")) {
        return new org.graphstream.stream.file.FileSourceDOT();
    }
    if (flc.endsWith(".edge")) {
        return new FileSourceEdge();
    }
    if (flc.endsWith(".lgl")) {
        return new FileSourceLGL();
    }
    if (flc.endsWith(".ncol")) {
        return new FileSourceNCol();
    }
    if (flc.endsWith(".tlp")) {
        return new FileSourceTLP();
    }
    if (flc.endsWith(".xml")) {
        String root = getXMLRootElement(fileName);
        if (root.equalsIgnoreCase("gexf"))
            return new FileSourceGEXF();
        return new FileSourceGraphML();
    }
    if (flc.endsWith(".gexf")) {
        return new FileSourceGEXF();
    }
    return null;
}

public static String getXMLRootElement(String fileName) throws IOException {
    FileReader stream = new FileReader(fileName);
    XMLEventReader reader;
    XMLEvent e;
    String root;
    try {
        reader = XMLInputFactory.newInstance().createXMLEventReader(stream);
        do {
            e = reader.nextEvent();
        } while (!e.isStartElement() && !e.isEndDocument());
        if (e.isEndDocument())
            throw new IOException("document ended before catching root element");
        root = e.asStartElement().getName().getLocalPart();
        reader.close();
        stream.close();
        return root;
    } catch (XMLStreamException ex) {
        throw new IOException(ex);
    } catch (FactoryConfigurationError ex) {
        throw new IOException(ex);
    }
}

protected static String formatStringForQuoting(String str) {
    return str.replaceAll("(^|[^\\\\])\"", "$1\\\\\"");
}

protected static String attributeString(String key, Object value, boolean remove) {
    if (key == null || key.length() == 0)
        return null;
    if (remove) {
        return String.format(" -\"%s\"", key);
    } else {
        if (value != null && value.getClass().isArray())
            return String.format(" \"%s\":%s", key, arrayString(value));
        else
            return String.format(" \"%s\":%s", key, valueString(value));
    }
}

protected static String arrayString(Object value) {
    if (value != null && value.getClass().isArray()) {
        StringBuilder sb = new StringBuilder();
        sb.append("{");
        if (Array.getLength(value) == 0)
            sb.append("\"\"");
        else
            sb.append(arrayString(Array.get(value, 0)));
        for (int i = 1; i < Array.getLength(value); ++i) sb.append(String.format(",%s", arrayString(Array.get(value, i))));
        sb.append("}");
        return sb.toString();
    } else {
        return valueString(value);
    }
}

protected static String valueString(Object value) {
    if (value == null)
        return "\"\"";
    if (value instanceof CharSequence) {
        if (value instanceof String)
            return String.format("\"%s\"", formatStringForQuoting((String) value));
        else
            return String.format("\"%s\"", (CharSequence) value);
    } else if (value instanceof Number) {
        Number nval = (Number) value;
        if (value instanceof Integer || value instanceof Short || value instanceof Byte || value instanceof Long)
            return String.format(Locale.US, "%d", nval.longValue());
        else
            return String.format(Locale.US, "%f", nval.doubleValue());
    } else if (value instanceof Boolean) {
        return String.format(Locale.US, "%b", ((Boolean) value));
    } else if (value instanceof Character) {
        return String.format("\"%c\"", ((Character) value).charValue());
    } else if (value instanceof Object[]) {
        Object[] array = (Object[]) value;
        int n = array.length;
        StringBuffer sb = new StringBuffer();
        if (array.length > 0)
            sb.append(valueString(array[0]));
        for (int i = 1; i < n; i++) {
            sb.append(",");
            sb.append(valueString(array[i]));
        }
        return sb.toString();
    } else if (value instanceof HashMap<?, ?> || value instanceof CompoundAttribute) {
        HashMap<?, ?> hash;
        if (value instanceof CompoundAttribute)
            hash = ((CompoundAttribute) value).toHashMap();
        else
            hash = (HashMap<?, ?>) value;
        return hashToString(hash);
    } else if (value instanceof Color) {
        Color c = (Color) value;
        return String.format("#%02X%02X%02X%02X", c.getRed(), c.getGreen(), c.getBlue(), c.getAlpha());
    } else {
        return String.format("\"%s\"", value.toString());
    }
}

protected static String hashToString(HashMap<?, ?> hash) {
    StringBuilder sb = new StringBuilder();
    sb.append("[ ");
    for (Object key : hash.keySet()) {
        sb.append(attributeString(key.toString(), hash.get(key), false));
        sb.append(",");
    }
    sb.append(']');
    return sb.toString();
}

