/**
 * Count step contains in a file.
 *
 * @param path
 *            path to the file
 * @return count of step event in the file
 * @throws IOException
 * @see org.graphstream.stream.file.FileSourceFactory
 */
public static int countStepInFile(String path) throws IOException {
    StepCounter counter = new StepCounter();
    FileSource source = FileSourceFactory.sourceFor(path);
    source.addElementSink(counter);
    source.readAll(path);
    return counter.getStepCount();
}

@SuppressWarnings("unchecked")
protected static HashMap<String, Object> GET(HttpExchange ex) {
    HashMap<String, Object> get = new HashMap<String, Object>();
    String[] args = ex.getRequestURI().getRawQuery().split("[&]");
    for (String arg : args) {
        String[] kv = arg.split("[=]");
        String k, v;
        k = null;
        v = null;
        try {
            if (kv.length > 0)
                k = URLDecoder.decode(kv[0], System.getProperty("file.encoding"));
            if (kv.length > 1)
                v = URLDecoder.decode(kv[1], System.getProperty("file.encoding"));
            if (get.containsKey(k)) {
                Object o = get.get(k);
                if (o instanceof LinkedList<?>)
                    ((LinkedList<Object>) o).add(v);
                else {
                    LinkedList<Object> l = new LinkedList<Object>();
                    l.add(o);
                    l.add(v);
                    get.put(k, l);
                }
            } else {
                get.put(k, v);
            }
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
        }
    }
    return get;
}

/**
 * Looks at the file name given and its extension and propose a file output
 * for the format that match this extension.
 *
 * @param filename
 *            The file name where the graph will be written.
 * @return A file sink or null.
 */
public static FileSink sinkFor(String filename) {
    if (filename.indexOf('.') > 0) {
        String ext = filename.substring(filename.lastIndexOf('.') + 1);
        ext = ext.toLowerCase();
        if (ext2sink.containsKey(ext)) {
            Class<? extends FileSink> fsink = ext2sink.get(ext);
            try {
                return fsink.newInstance();
            } catch (InstantiationException e) {
                e.printStackTrace();
            } catch (IllegalAccessException e) {
                e.printStackTrace();
            }
        }
    }
    return null;
}

