/**
 * Low-level access to decoding ASCII characters in
 * the form of a byte array. <strong>Ignores GUNZIP option, if
 * it's set.</strong> This is not generally a recommended method,
 * although it is used internally as part of the decoding process.
 * Special case: if len = 0, an empty array is returned. Still,
 * if you need more speed and reduced memory footprint (and aren't
 * gzipping), consider this method.
 *
 * @param source The Base64 encoded data
 * @param off    The offset of where to begin decoding
 * @param len    The length of characters to decode
 * @param options Can specify options such as alphabet type to use
 * @return decoded data
 * @throws java.io.IOException If bogus characters exist in source data
 * @since 1.3
 */
public static byte[] decode(byte[] source, int off, int len, int options) throws java.io.IOException {
    // Lots of error checking and exception throwing
    if (source == null) {
        throw new NullPointerException("Cannot decode null source array.");
    }
    // end if
    if (off < 0 || off + len > source.length) {
        throw new IllegalArgumentException(String.format("Source array with length %d cannot have offset of %d and process %d bytes.", source.length, off, len));
    }
    // end if
    if (len == 0) {
        return new byte[0];
    } else if (len < 4) {
        throw new IllegalArgumentException("Base64-encoded string must have at least four characters, but length specified was " + len);
    }
    // end if
    byte[] DECODABET = getDecodabet(options);
    // Estimate on array size
    int len34 = len * 3 / 4;
    // Upper limit on size of output
    byte[] outBuff = new byte[len34];
    // Keep track of where we're writing
    int outBuffPosn = 0;
    // Four byte buffer from source, eliminating white space
    byte[] b4 = new byte[4];
    // Keep track of four byte input buffer
    int b4Posn = 0;
    // Source array counter
    int i = 0;
    // Special value from DECODABET
    byte sbiDecode = 0;
    for (i = off; i < off + len; i++) {
        // Loop through source
        sbiDecode = DECODABET[source[i] & 0xFF];
        // White space, Equals sign, or legit Base64 character
        // Note the values such as -5 and -9 in the
        // DECODABETs at the top of the file.
        if (sbiDecode >= WHITE_SPACE_ENC) {
            if (sbiDecode >= EQUALS_SIGN_ENC) {
                // Save non-whitespace
                b4[b4Posn++] = source[i];
                if (b4Posn > 3) {
                    // Time to decode?
                    outBuffPosn += decode4to3(b4, 0, outBuff, outBuffPosn, options);
                    b4Posn = 0;
                    // If that was the equals sign, break out of 'for' loop
                    if (source[i] == EQUALS_SIGN) {
                        break;
                    }
                    // end if: equals sign
                }
                // end if: quartet built
            }
            // end if: equals sign or better
        } else // end if: white space, equals sign or better
        {
            // There's a bad input character in the Base64 stream.
            throw new java.io.IOException(String.format("Bad Base64 input character decimal %d in array position %d", ((int) source[i]) & 0xFF, i));
        }
        // end else:
    }
    // each input character
    byte[] out = new byte[outBuffPosn];
    System.arraycopy(outBuff, 0, out, 0, outBuffPosn);
    return out;
}

