/**
 * Access to the global shared environment for the whole JVM. This method
 * allows to access a shared environment, that can be read and written from
 * anywhere.
 * @return A singleton instance of the global environment.
 */
public static Environment getGlobalEnvironment() {
    if (GLOBAL_ENV == null)
        GLOBAL_ENV = new Environment();
    return GLOBAL_ENV;
}

public static <T extends Element> Filter<T> falseFilter() {
    return new Filter<T>() {

        public boolean isAvailable(T e) {
            return false;
        }
    };
}

public static <T extends Element> Filter<T> trueFilter() {
    return new Filter<T>() {

        public boolean isAvailable(T e) {
            return true;
        }
    };
}

public static <T extends Element> Filter<T> byAttributeFilter(String key, Object expectedValue) {
    return new ByAttributeFilter<T>(key, expectedValue);
}

public static <T extends Element, U extends Element> Filter<Element> separateNodeAndEdgeFilter(Filter<T> nodeFilter, Filter<U> edgeFilter) {
    return new SeparateNodeEdgeFilter<T, U>(nodeFilter, edgeFilter);
}

public static <T extends Element, U extends Element> Filter<T> byExtremitiesFilter(Filter<U> f) {
    return new ExtremitiesFilter<T, U>(f);
}

public static <T extends Element> Filter<T> byIdFilter(String idPattern) {
    return new ByIdFilter<T>(idPattern);
}

public static <T extends Element> Filter<T> isContained(final Collection<? extends T> set) {
    return new Filter<T>() {

        public boolean isAvailable(T e) {
            return set.contains(e);
        }
    };
}

public static <T extends Element> Filter<T> isIdContained(final Collection<String> set) {
    return new Filter<T>() {

        public boolean isAvailable(T e) {
            return set.contains(e.getId());
        }
    };
}

public static <T extends Element> Filter<T> and(Filter<T> f1, Filter<T> f2) {
    return new AndFilter<T>(f1, f2);
}

public static <T extends Element> Filter<T> or(Filter<T> f1, Filter<T> f2) {
    return new OrFilter<T>(f1, f2);
}

public static <T extends Element> Filter<T> xor(Filter<T> f1, Filter<T> f2) {
    return new XorFilter<T>(f1, f2);
}

public static <T extends Element> Filter<T> not(Filter<T> f) {
    return new NotFilter<T>(f);
}

/**
 * Replaces unprintable characters by their escaped (or unicode escaped)
 * equivalents in the given string
 */
protected static final String addEscapes(String str) {
    StringBuffer retval = new StringBuffer();
    char ch;
    for (int i = 0; i < str.length(); i++) {
        switch(str.charAt(i)) {
            case 0:
                continue;
            case '\b':
                retval.append("\\b");
                continue;
            case '\t':
                retval.append("\\t");
                continue;
            case '\n':
                retval.append("\\n");
                continue;
            case '\f':
                retval.append("\\f");
                continue;
            case '\r':
                retval.append("\\r");
                continue;
            case '\"':
                retval.append("\\\"");
                continue;
            case '\'':
                retval.append("\\\'");
                continue;
            case '\\':
                retval.append("\\\\");
                continue;
            default:
                if ((ch = str.charAt(i)) < 0x20 || ch > 0x7e) {
                    String s = "0000" + Integer.toString(ch, 16);
                    retval.append("\\u" + s.substring(s.length() - 4, s.length()));
                } else {
                    retval.append(ch);
                }
                continue;
        }
    }
    return retval.toString();
}

/**
 * Returns a detailed message for the Error when it is thrown by the token
 * manager to indicate a lexical error. Parameters : EOFSeen : indicates if
 * EOF caused the lexical error curLexState : lexical state in which this
 * error occurred errorLine : line number when the error occurred
 * errorColumn : column number when the error occurred errorAfter : prefix
 * that was seen before this error occurred curchar : the offending
 * character Note: You can customize the lexical error message by modifying
 * this method.
 */
protected static String LexicalError(boolean EOFSeen, int lexState, int errorLine, int errorColumn, String errorAfter, char curChar) {
    return ("Lexical error at line " + errorLine + ", column " + errorColumn + ".  Encountered: " + (EOFSeen ? "<EOF> " : ("\"" + addEscapes(String.valueOf(curChar)) + "\"") + " (" + (int) curChar + "), ") + "after : \"" + addEscapes(errorAfter) + "\"");
}

