public String toString(int level) {
    StringBuilder builder = new StringBuilder();
    String prefix = "";
    String sprefix = "    ";
    if (level > 0) {
        for (int i = 0; i < level; i++) prefix += "    ";
    }
    // builder.append( String.format( "%s%s%n", prefix, super.toString() )
    // );
    if (parent != null) {
        Rule p = parent;
        while (!(p == null)) {
            builder.append(String.format(" -> %s", p.selector.toString()));
            p = p.getStyle().getParent();
        }
    }
    if (alternates != null && alternates.size() > 0) {
        builder.append(String.format(" /"));
        for (Rule rule : alternates.values()) {
            builder.append(' ');
            builder.append(rule.selector.toString());
        }
    }
    builder.append(String.format("%n"));
    Iterator<String> i = values.keySet().iterator();
    while (i.hasNext()) {
        String key = i.next();
        Object o = values.get(key);
        if (o instanceof ArrayList<?>) {
            ArrayList<?> array = (ArrayList<?>) o;
            if (array.size() > 0) {
                builder.append(String.format("%s%s%s%s: ", prefix, sprefix, sprefix, key));
                for (Object p : array) builder.append(String.format("%s ", p.toString()));
                builder.append(String.format("%n"));
            } else {
                builder.append(String.format("%s%s%s%s: <empty>%n", prefix, sprefix, sprefix, key));
            }
        } else {
            builder.append(String.format("%s%s%s%s: %s%n", prefix, sprefix, sprefix, key, o != null ? o.toString() : "<null>"));
        }
    }
    /*
		 * if( level >= 0 ) { if( parent != null ) { String rec =
		 * parent.style.toString( level + 1 );
		 * 
		 * builder.append( rec ); } }
		 */
    String res = builder.toString();
    if (res.length() == 0)
        return String.format("%s%s<empty>%n", prefix, prefix);
    return res;
}

