protected Object getValueForEvent(String property, String event) {
    if (alternates != null) {
        Rule rule = alternates.get(event);
        if (rule != null) {
            Object o = rule.getStyle().values.get(property);
            if (o != null)
                return o;
        }
    } else if (parent != null) {
        return parent.style.getValueForEvent(property, event);
    }
    return null;
}

