/**
 * Generate a gradient in the given pixel area following the given style.
 * This produces a gradient only if the style fill-mode is compatible.
 *
 * @param x0
 *            The left corner of the area.
 * @param y0
 *            The bottom corner of the area.
 * @param width
 *            The area width.
 * @param height
 *            The area height.
 * @param style
 *            The style.
 * @return A gradient paint or null if the style does not specify a
 *         gradient.
 */
public static Paint gradientInArea(int x0, int y0, int width, int height, Style style) {
    switch(style.getFillMode()) {
        case GRADIENT_VERTICAL:
            return linearGradientFromStyle(x0, y0, x0, y0 + height, style);
        case GRADIENT_HORIZONTAL:
            return linearGradientFromStyle(x0, y0, x0 + width, y0, style);
        case GRADIENT_DIAGONAL1:
            return linearGradientFromStyle(x0, y0, x0 + width, y0 + height, style);
        case GRADIENT_DIAGONAL2:
            return linearGradientFromStyle(x0 + width, y0, x0, y0 + height, style);
        case GRADIENT_RADIAL:
            return radialGradientFromStyle(x0 + (width / 2), y0 + (height / 2), width > height ? width / 2 : height / 2, style);
        default:
            return null;
    }
}

/**
 * Generate a linear gradient between two given points corresponding to the
 * given style.
 *
 * @param x0
 *            The start point abscissa.
 * @param y0
 *            The start point ordinate.
 * @param x1
 *            The end point abscissa.
 * @param y1
 *            The end point ordinate.
 * @param style
 *            The style.
 * @return A paint for the gradient or null if the style specifies no
 *         gradient (the fill mode is not a linear gradient or there is only
 *         one fill colour).
 */
public static Paint linearGradientFromStyle(float x0, float y0, float x1, float y1, Style style) {
    Paint paint = null;
    if (style.getFillColorCount() > 1) {
        switch(style.getFillMode()) {
            case GRADIENT_DIAGONAL1:
            case GRADIENT_DIAGONAL2:
            case GRADIENT_HORIZONTAL:
            case GRADIENT_VERTICAL:
                if (version16)
                    paint = new LinearGradientPaint(x0, y0, x1, y1, createFractions(style), createColors(style));
                else
                    paint = new GradientPaint(x0, y0, style.getFillColor(0), x1, y1, style.getFillColor(1));
                break;
            default:
                break;
        }
    }
    return paint;
}

public static Paint radialGradientFromStyle(float cx, float cy, float radius, Style style) {
    return radialGradientFromStyle(cx, cy, radius, cx, cy, style);
}

/**
 * Generate a radial gradient between whose center is at (cx,cy) with the
 * given radius. The focus (fx,fy) is the start position of the gradient in
 * the circle.
 *
 * @param cx
 *            The center point abscissa.
 * @param cy
 *            The center point ordinate.
 * @param fx
 *            The start point abscissa.
 * @param fy
 *            The start point ordinate.
 * @param radius
 *            The gradient radius.
 * @param style
 *            The style.
 * @return A paint for the gradient or null if the style specifies no
 *         gradient (the fill mode is not a radial gradient or there is only
 *         one fill colour).
 */
public static Paint radialGradientFromStyle(float cx, float cy, float radius, float fx, float fy, Style style) {
    Paint paint = null;
    if (version16) {
        if (style.getFillColorCount() > 1 && style.getFillMode() == FillMode.GRADIENT_RADIAL) {
            float[] fractions = createFractions(style);
            Color[] colors = createColors(style);
            paint = new RadialGradientPaint(cx, cy, radius, fx, fy, fractions, colors, MultipleGradientPaint.CycleMethod.REFLECT);
        }
    }
    return paint;
}

protected static float[] createFractions(Style style) {
    int n = style.getFillColorCount();
    if (n < predefFractions.length)
        return predefFractions[n];
    float[] fractions = new float[n];
    float div = 1f / (n - 1);
    for (int i = 1; i < (n - 1); i++) fractions[i] = div * i;
    fractions[0] = 0f;
    fractions[n - 1] = 1f;
    return fractions;
}

protected static Color[] createColors(Style style) {
    int n = style.getFillColorCount();
    Color[] colors = new Color[n];
    for (int i = 0; i < n; i++) colors[i] = style.getFillColor(i);
    return colors;
}

