public void writeParameterFile(String fileName) throws IOException{
    BufferedWriter bw = new BufferedWriter(new FileWriter(fileName));
    Set<String> ks = parameters.keySet();
    for (String key : ks) {
        bw.write(key + " = " + parameters.get(key));
        bw.newLine();
    }
    bw.close();
}

