/**
 * Decodes data from Base64 notation, automatically
 * detecting gzip-compressed data and decompressing it.
 *
 * @param s the string to decode
 * @param options encode options such as URL_SAFE
 * @return the decoded data
 * @throws java.io.IOException if there is an error
 * @throws NullPointerException if <tt>s</tt> is null
 * @since 1.4
 */
public static byte[] decode(String s, int options) throws java.io.IOException {
    if (s == null) {
        throw new NullPointerException("Input string was null.");
    }
    // end if
    byte[] bytes;
    try {
        bytes = s.getBytes(PREFERRED_ENCODING);
    }// end try
     catch (java.io.UnsupportedEncodingException uee) {
        bytes = s.getBytes();
    }
    // end catch
    //</change>
    // Decode
    bytes = decode(bytes, 0, bytes.length, options);
    // Check to see if it's gzip-compressed
    // GZIP Magic Two-Byte Number: 0x8b1f (35615)
    boolean dontGunzip = (options & DONT_GUNZIP) != 0;
    if ((bytes != null) && (bytes.length >= 4) && (!dontGunzip)) {
        int head = ((int) bytes[0] & 0xff) | ((bytes[1] << 8) & 0xff00);
        if (java.util.zip.GZIPInputStream.GZIP_MAGIC == head) {
            java.io.ByteArrayInputStream bais = null;
            java.util.zip.GZIPInputStream gzis = null;
            java.io.ByteArrayOutputStream baos = null;
            byte[] buffer = new byte[2048];
            int length = 0;
            try {
                baos = new java.io.ByteArrayOutputStream();
                bais = new java.io.ByteArrayInputStream(bytes);
                gzis = new java.util.zip.GZIPInputStream(bais);
                while ((length = gzis.read(buffer)) >= 0) {
                    baos.write(buffer, 0, length);
                }
                // end while: reading input
                // No error? Get new bytes.
                bytes = baos.toByteArray();
            }// end try
             catch (java.io.IOException e) {
                e.printStackTrace();
                // Just return originally-decoded bytes
            } finally // end catch
            {
                try {
                    baos.close();
                } catch (Exception e) {
                }
                try {
                    gzis.close();
                } catch (Exception e) {
                }
                try {
                    bais.close();
                } catch (Exception e) {
                }
            }
            // end finally
        }
        // end if: gzipped
    }
    // end if: bytes.length >= 2
    return bytes;
}

// end decode
/**
 * Attempts to decode Base64 data and deserialize a Java
 * Object within. Returns <tt>null</tt> if there was an error.
 *
 * @param encodedObject The Base64 data to decode
 * @return The decoded and deserialized object
 * @throws NullPointerException if encodedObject is null
 * @throws java.io.IOException if there is a general error
 * @throws ClassNotFoundException if the decoded object is of a
 *         class that cannot be found by the JVM
 * @since 1.5
 */
public static Object decodeToObject(String encodedObject) throws java.io.IOException, java.lang.ClassNotFoundException {
    return decodeToObject(encodedObject, NO_OPTIONS, null);
}

/**
 * Attempts to decode Base64 data and deserialize a Java
 * Object within. Returns <tt>null</tt> if there was an error.
 * If <tt>loader</tt> is not null, it will be the class loader
 * used when deserializing.
 *
 * @param encodedObject The Base64 data to decode
 * @param options Various parameters related to decoding
 * @param loader Optional class loader to use in deserializing classes.
 * @return The decoded and deserialized object
 * @throws NullPointerException if encodedObject is null
 * @throws java.io.IOException if there is a general error
 * @throws ClassNotFoundException if the decoded object is of a
 *         class that cannot be found by the JVM
 * @since 2.3.4
 */
public static Object decodeToObject(String encodedObject, int options, final ClassLoader loader) throws java.io.IOException, java.lang.ClassNotFoundException {
    // Decode and gunzip if necessary
    byte[] objBytes = decode(encodedObject, options);
    java.io.ByteArrayInputStream bais = null;
    java.io.ObjectInputStream ois = null;
    Object obj = null;
    try {
        bais = new java.io.ByteArrayInputStream(objBytes);
        // If no custom class loader is provided, use Java's builtin OIS.
        if (loader == null) {
            ois = new java.io.ObjectInputStream(bais);
        } else // end if: no loader provided
        // Else make a customized object input stream that uses
        // the provided class loader.
        {
            ois = new java.io.ObjectInputStream(bais) {

                @Override
                public Class<?> resolveClass(java.io.ObjectStreamClass streamClass) throws java.io.IOException, ClassNotFoundException {
                    Class<?> c = Class.forName(streamClass.getName(), false, loader);
                    if (c == null) {
                        return super.resolveClass(streamClass);
                    } else {
                        // Class loader knows of this class.
                        return c;
                    }
                    // end else: not null
                }
            };
            // end ois
        }
        // end else: no custom class loader
        obj = ois.readObject();
    }// end try
     catch (java.io.IOException e) {
        // Catch and throw in order to execute finally{}
        throw e;
    }// end catch
     catch (java.lang.ClassNotFoundException e) {
        // Catch and throw in order to execute finally{}
        throw e;
    } finally // end catch
    {
        try {
            bais.close();
        } catch (Exception e) {
        }
        try {
            ois.close();
        } catch (Exception e) {
        }
    }
    // end finally
    return obj;
}

// end decodeToFile
/**
 * Convenience method for reading a base64-encoded
 * file and decoding it.
 *
 * <p>As of v 2.3, if there is a error,
 * the method will throw an java.io.IOException. <b>This is new to v2.3!</b>
 * In earlier versions, it just returned false, but
 * in retrospect that's a pretty poor way to handle it.</p>
 *
 * @param filename Filename for reading encoded data
 * @return decoded byte array
 * @throws java.io.IOException if there is an error
 * @since 2.1
 */
public static byte[] decodeFromFile(String filename) throws java.io.IOException {
    byte[] decodedData = null;
    Base64.InputStream bis = null;
    try {
        // Set up some useful variables
        java.io.File file = new java.io.File(filename);
        byte[] buffer = null;
        int length = 0;
        int numBytes = 0;
        // Check for size of file
        if (file.length() > Integer.MAX_VALUE) {
            throw new java.io.IOException("File is too big for this convenience method (" + file.length() + " bytes).");
        }
        // end if: file too big for int index
        buffer = new byte[(int) file.length()];
        // Open a stream
        bis = new Base64.InputStream(new java.io.BufferedInputStream(new java.io.FileInputStream(file)), Base64.DECODE);
        // Read until done
        while ((numBytes = bis.read(buffer, length, 4096)) >= 0) {
            length += numBytes;
        }
        // end while
        // Save in a variable to return
        decodedData = new byte[length];
        System.arraycopy(buffer, 0, decodedData, 0, length);
    }// end try
     catch (java.io.IOException e) {
        // Catch and release to execute finally{}
        throw e;
    } finally // end catch: java.io.IOException
    {
        try {
            bis.close();
        } catch (Exception e) {
        }
    }
    // end finally
    return decodedData;
}

// end decodeFromFile
/**
 * Convenience method for reading a binary file
 * and base64-encoding it.
 *
 * <p>As of v 2.3, if there is a error,
 * the method will throw an java.io.IOException. <b>This is new to v2.3!</b>
 * In earlier versions, it just returned false, but
 * in retrospect that's a pretty poor way to handle it.</p>
 *
 * @param filename Filename for reading binary data
 * @return base64-encoded string
 * @throws java.io.IOException if there is an error
 * @since 2.1
 */
public static String encodeFromFile(String filename) throws java.io.IOException {
    String encodedData = null;
    Base64.InputStream bis = null;
    try {
        // Set up some useful variables
        java.io.File file = new java.io.File(filename);
        // Need max() for math on small files (v2.2.1); Need +1 for a few corner cases (v2.3.5)
        byte[] buffer = new byte[Math.max((int) (file.length() * 1.4 + 1), 40)];
        int length = 0;
        int numBytes = 0;
        // Open a stream
        bis = new Base64.InputStream(new java.io.BufferedInputStream(new java.io.FileInputStream(file)), Base64.ENCODE);
        // Read until done
        while ((numBytes = bis.read(buffer, length, 4096)) >= 0) {
            length += numBytes;
        }
        // end while
        // Save in a variable to return
        encodedData = new String(buffer, 0, length, Base64.PREFERRED_ENCODING);
    }// end try
     catch (java.io.IOException e) {
        // Catch and release to execute finally{}
        throw e;
    } finally // end catch: java.io.IOException
    {
        try {
            bis.close();
        } catch (Exception e) {
        }
    }
    // end finally
    return encodedData;
}

public static Graph unmutableGraph(Graph g) {
    return null;
}

/**
 * Synchronizes a graph. The returned graph can be accessed and modified by
 * several threads. You lose genericity in methods returning edge or node
 * because each element (graph, nodes and edges) is wrapped into a
 * synchronized wrapper which breaks original elements class.
 *
 * @param g
 *            the graph to synchronize
 * @return a synchronized wrapper for g
 */
public static Graph synchronizedGraph(Graph g) {
    return new SynchronizedGraph(g);
}

/**
 * Merge several graphs in one. A new graph is created, that will contain
 * the result. The method will try to create a graph of the same class that
 * the first graph to merge (it needs to have a constructor with a String).
 * Else, a MultiGraph is used.
 *
 * @param graphs
 *            graphs to merge
 * @return merge result
 */
public static Graph merge(Graph... graphs) {
    if (graphs == null)
        return new DefaultGraph("void-merge");
    String id = "merge";
    for (Graph g : graphs) id += "-" + g.getId();
    Graph result;
    try {
        Class<? extends Graph> cls = graphs[0].getClass();
        result = cls.getConstructor(String.class).newInstance(id);
    } catch (Exception e) {
        logger.warning(String.format("Cannot create a graph of %s.", graphs[0].getClass().getName()));
        result = new MultiGraph(id);
    }
    mergeIn(result, graphs);
    return result;
}

/**
 * Clone a given graph with same node/edge structure and same attributes.
 *
 * @param g
 *            the graph to clone
 * @return a copy of g
 */
public static Graph clone(Graph g) {
    Graph copy;
    try {
        Class<? extends Graph> cls = g.getClass();
        copy = cls.getConstructor(String.class).newInstance(g.getId());
    } catch (Exception e) {
        logger.warning(String.format("Cannot create a graph of %s.", g.getClass().getName()));
        copy = new AdjacencyListGraph(g.getId());
    }
    copyAttributes(g, copy);
    for (int i = 0; i < g.getNodeCount(); i++) {
        Node source = g.getNode(i);
        Node target = copy.addNode(source.getId());
        copyAttributes(source, target);
    }
    for (int i = 0; i < g.getEdgeCount(); i++) {
        Edge source = g.getEdge(i);
        Edge target = copy.addEdge(source.getId(), source.getSourceNode().getId(), source.getTargetNode().getId(), source.isDirected());
        copyAttributes(source, target);
    }
    return copy;
}

