/**
 * Joins two Fibonacci heaps into a new one. No heap consolidation is
 * performed at this time. The two root lists are simply joined together.
 *
 * <p>Running time: O(1) actual</p>
 *
 * @param h1 first heap
 * @param h2 second heap
 *
 * @return new heap containing h1 and h2
 */
public static <T> FibonacciHeap<T> union(FibonacciHeap<T> h1, FibonacciHeap<T> h2) {
    FibonacciHeap<T> h = new FibonacciHeap<T>();
    if ((h1 != null) && (h2 != null)) {
        h.minNode = h1.minNode;
        if (h.minNode != null) {
            if (h2.minNode != null) {
                h.minNode.right.left = h2.minNode.left;
                h2.minNode.left.right = h.minNode.right;
                h.minNode.right = h2.minNode;
                h2.minNode.left = h.minNode;
                if (h2.minNode.key < h1.minNode.key) {
                    h.minNode = h2.minNode;
                }
            }
        } else {
            h.minNode = h2.minNode;
        }
        h.nNodes = h1.nNodes + h2.nNodes;
    }
    return h;
}

/**
 * Casts an object to a type.
 *
 * @param o object to be cast
 * @param typeDecl conveys the target type information; the actual value is
 * unused and can be null since this is all just stupid compiler tricks
 *
 * @return the result of the cast
 */
@SuppressWarnings("unchecked")
public static <T> T uncheckedCast(Object o, TypeUtil<T> typeDecl) {
    return (T) o;
}

public static long factorial(int N) {
    long multi = 1;
    for (int i = 1; i <= N; i++) {
        multi = multi * i;
    }
    return multi;
}

